import React, { memo, Component } from "react";
import { Button, Icon } from "semantic-ui-react";
import { ROUTES, SIZES } from "./../../constants";
import apiHelper from "./../../utils/UiApiHelper";
import { connect } from "react-redux";

class HeaderBar extends Component {
    constructor(props) {
        super(props)
    }

    shouldShowBtn = (path) => {
        return path !== window.location.pathname;
    };

    handleLogOut = (history) => {
        apiHelper.logoutUser().then(
            () => history.push(ROUTES.SIGNIN),
            err => console.error(err)
        );
    };

    render(){
        const { history } = this.props;
        return (
            <section className="header-bar">
                {this.shouldShowBtn(ROUTES.HOME) && (
                    <Button animated size={SIZES.LARGE} onClick={() => history.push(ROUTES.HOME)}>
                        <Button.Content visible>Add URL</Button.Content>
                        <Button.Content hidden>
                            <Icon name="save" />
                        </Button.Content>
                    </Button>
                )}

                {this.shouldShowBtn(ROUTES.LIST) && (
                    <Button animated size={SIZES.LARGE} onClick={() => history.push(ROUTES.LIST)}>
                        <Button.Content visible>List of URLs</Button.Content>
                        <Button.Content hidden>
                            <Icon name="arrow right" />
                        </Button.Content>
                    </Button>
                )}

                {this.shouldShowBtn(ROUTES.WORDS) && (
                    <Button animated="fade" size={SIZES.LARGE} onClick={() => history.push(ROUTES.WORDS)}>
                        <Button.Content visible>Stored Worlds</Button.Content>
                        <Button.Content hidden>
                            <Icon name="bullhorn" />
                        </Button.Content>
                    </Button>
                )}

                <Button animated="fade" size={SIZES.LARGE} onClick={() => this.handleLogOut(history)}>
                    <Button.Content visible>Log out</Button.Content>
                    <Button.Content hidden>
                        <Icon name="log out" />
                    </Button.Content>
                </Button>

            </section>
        );
    }
};


const mapStateToProps = ({token}) => ({
    token,
});


export default memo(connect(mapStateToProps, null)(HeaderBar));
