// withAuth.jsx
import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';

export default function withAuth(ComponentToProtect, path, isPrivatePath, redirect) {
    console.log('here');

    return class extends Component {
        constructor(props) {
            super(props);
            this.state = {
                loading: true,
                path,
                isPrivatePath,
            };
        }

        componentDidMount() {
            const { privatePath } = this.state;
            if (privatePath) {
                fetch('/checkToken')
                    .then(res => {
                        if (res.status === 200) {
                            this.setState({loading: false});
                        } else {
                            throw new Error(res.error);
                        }
                    })
                    .catch(err => {
                        console.error(err);
                        this.setState({loading: false, redirect: true});
                    });
            }

        }

        render() {
            const {loading } = this.state;
            if (loading) {
                return null;
            }
            // if (redirect) {
            //     redirect = false;
            //     return <Redirect to="/signin"/>;
            // }
            return (
                <React.Fragment>
                    <ComponentToProtect {...this.props} />
                </React.Fragment>
            );
        }
    }
}
