import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import apiHelper from "../../utils/UiApiHelper";

export default function withAuth(ComponentToProtect, isPrivatePath, redirect) {
    return class extends Component {
        constructor() {
            super();
            this.state = {
                isTokenValid: false,
                loading: true,
            };

        }

        componentWillUpdate() {
            apiHelper.checkToken()
                .then(res => {
                    if (res.status === 200) {
                        console.log('check');
                        this.setState({ isTokenValid: true });
                        console.log(this.state)
                    } else {
                        const error = new Error(res.error);
                        throw error;
                    }
                })
                .catch(err => {
                    console.error(err);
                    //this.setState({ isTokenValid: false });
                    // this.setState({ loading: false });
                });
        };

        render() {
            const { isTokenValid } = this.state;
            if (!isTokenValid && isPrivatePath) {
                console.log('render2')
                console.log(isTokenValid)
                return <Redirect to="/signin" />;
            } else {
                console.log('render1')
                return (
                    <ComponentToProtect {...this.props} />
                );
            }

        }
    }
}
