import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {connect} from "react-redux";
import {setWhetherIsValid, setWhetherIsChecked} from "./../../actions/tokenActions";

export default function withAuth(ComponentToProtect, isPrivatePath) {
    class PageComponent extends Component {
        constructor() {
            super();
            this.state = {
                isTokenValid: false,
            };

        }

        componentWillMount() {
            if (isPrivatePath) {
                this.props.checkToken()
                    .then(() => {
                        this.setState({isTokenValid: true})
                    })
                    .catch(err => {
                        console.error(err);
                    });
            }
        };

        render() {
            const {token} = this.props;
            if (!isPrivatePath || (token.isChecked && token.isTokenValid)) {
                return (
                    <ComponentToProtect {...this.props} />
                );
            } else if (!token.isChecked) {
                return null;
            } else if (!token.isTokenValid && isPrivatePath && token.isChecked) {
                return <Redirect to="/signin"/>;
            }
        }
    }

    const mapStateToProps = ({token}) => ({
        token,
    });

    const mapDispatchToProps = dispatch => ({
        checkToken: () => dispatch(setWhetherIsValid()),
        setWeatherIsChecked: (value) => dispatch(setWhetherIsChecked(value)),
    });

    return connect(mapStateToProps, mapDispatchToProps)(PageComponent);
}
