class StatusesHelper {

    markStatusesAsSaved = (list, url, server) => {
        return list.map( item => {
            if(server === item.serverName && url === item.url) {
                item.isSaved = true;
            }
            return item;
        });
    };
    markStatusesAsFailed = (list, url, server) => {
        return list.map( item => {
            if(server === item.serverName && url === item.url) {
                item.isFailed = true;
            }
            return item;
        });
    };
}

export default new StatusesHelper();
