import React from "react";
import ReactDOM from "react-dom";
import { Provider } from "react-redux";
import { BrowserRouter, Route, Redirect } from "react-router-dom";
//import { renderRoutes } from "react-router-config";
import { ROUTES } from "./constants";
import withAuth from './common/withAuth/withAuth';

import configureStore from "./store/store";
import * as serviceWorker from "./serviceWorker";
import routes from "./routes";

import "./styles/main.scss";
import "semantic-ui-css/semantic.min.css";

const store = configureStore();
const isLoggedIn = false;

// const PrivateRoute = ({ component: Component, ...rest }) => {
//     return <Route {...rest} render={(props) => {
//         if (isLoggedIn || !rest.redirect) {
//             return <Component {...props} />
//         } else if (rest.redirect) {
//             rest.redirect = false;
//             return <Redirect to='/signin' exact/>
//         }
//     }} />
// };

ReactDOM.render(
  <Provider store={store}>
    <BrowserRouter>
        {renderRoutes(routes[0].routes)}
    </BrowserRouter>
  </Provider>,
  document.getElementById("root")
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();


function renderRoutes(routes) {
    return <div>
        {goThroughRoutes(routes)}
    </div>
}

// function goThroughRoutes(routes) {
//     return routes.map((route) => {
//         return <PrivateRoute path={route.path} component={route.component} refirect={route.redirect} exact/>;
//     });
// }

function goThroughRoutes(routes) {
    return routes.map((route) => {
        return <Route path={route.path} component={withAuth(route.component)} exact />;
    });
}
