import apiHelper from "../utils/UiApiHelper";

export const Types = {
    SET_WEATHER_IS_VALID: "SET_WEATHER_IS_VALID",
};

export const isTokenValid = isTokenValid => ({
    type: Types.SET_WEATHER_IS_VALID,
    isTokenValid
});


export const setWeatherIsValid = () => async dispatch => {
    try {
       const isValid = await apiHelper.checkToken();
       dispatch(isTokenValid(isValid));
    } catch {
        isTokenValid(false);
    }
};
