import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {connect} from "react-redux";
import {setWeatherIsValid} from "./../../actions/tokenActions";

export default function withAuth(ComponentToProtect, isPrivatePath) {
    class PageComponent extends Component {
        constructor() {
            super();
            this.state = {
                isTokenValid: false,
                loading: true,
            };

        }

        componentWillMount() {
            this.props.checkToken()
                .then(res => {
                    if (this.props.token.isTokenValid) {
                        this.setState({isTokenValid: true});
                    } else {
                        const error = new Error(res.error);
                        throw error;
                    }
                })
                .catch(err => {
                    console.error(err);
                });
        };

        render() {
            const {isTokenValid} = this.state;
            const {token} = this.props;
            if (!isPrivatePath) {
                return (
                    <ComponentToProtect {...this.props} />
                );
            } else if (!token.isChecked && !isTokenValid && isPrivatePath) {
                return null;
            } else if (!isTokenValid && isPrivatePath && token.isChecked) {
                return <Redirect to="/signin"/>;
            }

        }
    }

    const mapStateToProps = ({token}) => ({
        token,
    });

    const mapDispatchToProps = dispatch => ({
        checkToken: () => dispatch(setWeatherIsValid())
    });

    return connect(mapStateToProps, mapDispatchToProps)(PageComponent);
}
