import { Redirect } from 'react-router-dom';
import apiHelper from "../../utils/UiApiHelper";

export default function withAuth(ComponentToProtect, isPrivatePath, redirect) {
    return class extends Component {
        constructor() {
            super();
            this.state = {
                isTokenValid: false,
                loading: true,
            };

            this.checkToken();
        }

        checkToken = () => {
            apiHelper.checkToken()
                .then(res => {
                    if (res.status === 200) {
                        //this.setState({ loading: false });
                    } else {
                        const error = new Error(res.error);
                        throw error;
                    }
                })
                .catch(err => {
                    console.error(err);
                    // this.setState({ isTokenValid: false });
                    // this.setState({ loading: false });
                });
        };

        render() {
            const { isTokenValid } = this.state;
            // if (!isTokenValid && isPrivatePath && redirect) {
            //     redirect = false;
            //     return <Redirect to="/signin" />;
            // } else {
                return (
                    <ComponentToProtect {...this.props} />
                );
            //}

        }
    }
}
