// withAuth.jsx
import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';

export default function withAuth(ComponentToProtect, isPrivatePath) {
    return class extends Component {
        constructor() {
            super();
            this.state = {
                loading: true,
                isPrivatePath,
            };
        }

        componentDidMount() {
            const { privatePath } = this.state;
            if (privatePath) {
                fetch('/checkToken')
                    .then(res => {
                        if (res.status === 200) {
                            this.setState({loading: false});
                        } else {
                            throw new Error(res.error);
                        }
                    })
                    .catch(err => {
                        console.error(err);
                        this.setState({loading: false, redirect: true});
                    });
            }

        }

        render() {
            console.log('etwa')
            const {loading, redirect} = this.state;
            if (loading) {
                return null;
            }
            // if (redirect && isPrivatePath) {
            //     return <Redirect to="/signin" />;
            // }
            return (
                    <ComponentToProtect {...this.props} />
            );
        }
    }
}
