import { Types as TokenTypes } from "../actions/tokenActions";


const defaultState = {
    isTokenValid: false,
    isChecked: false,
};

const token = (state = defaultState, action) => {
    switch (action.type) {
        case TokenTypes.SET_WEATHER_IS_VALID:
            return Object.assign({}, {isTokenValid: action.isTokenValid} );
        default:
            return state;
    }
};

export default token;
